import React, { Component } from 'react'
import Header from '../../comps/header/Header'
import Footer from '../../comps/footer/Footer'
import Helmet from 'react-helmet'
import conf from '../../config.json'
import { Link } from 'react-router-dom'
import Mainbg from '../../assets/mainimg.svg'
import './detect.css'

export default class Detect extends Component {

    componentDidMount(){
        // this.enableVideo()

        // let video = document.getElementById('user-video')
        
    }

    state = {
        isCameraAccessed: true
    }

    captureVideo = () => {
        var canvas = document.getElementById('canvas');
        var video = document.getElementById('user-video');
        canvas.getContext('2d').drawImage(video, 0, 0, video.videoWidth, video.videoHeight);
    }

    enableVideo = async () => {
        let vid = document.getElementById("user-video")
        const constraints = {
            audio: false,
            video: true
        }
        try{
            const stream = await navigator.mediaDevices.getUserMedia( constraints )
            window.stream = stream
            vid.srcObject = stream
            vid.play()
        }
        catch( e ){

        }
    }


    render() {
        return(
            <React.Fragment>
                <Helmet>
                    <title>Detect Mask | {conf.APP_NAME}</title>
                </Helmet>
                <Header />
                
            <section>
          <div className="container mainsection">
            <div className="row">
              <div className="col col-12 col-lg-6" style={{'display': 'grid', 'placeItems': 'center'}}>
                <video src='https://gaanaworld.in/01.mp4' className="w-100" id="user-video" controls={ true } autoPlay={ false } />
                  <Link to="/detect" type="button" className="btn mainbtn mt-2">
                    Detect
                  </Link>
              </div>

              <div className="col col-12 col-lg-6 ">
                <div className="imgmain">
                    <canvas id="canvas"></canvas>
                  <img src={Mainbg} className="img-fluid homecol" id="user-img" alt="..." />
                </div>
              </div>
            </div>
          </div>
        </section>
                <Footer />
            </React.Fragment>
        )
    }
}
